  const addTodo=(title,desc)=>
  {
    console.log("I am adding todo ",Title,desc)
  }
  return (
  <>
<Header title= "Todo Lists" searchBar={true}/>
<AddTodo addTodo={addTodo}/>
<Todos todos={todos} onDelete={onDelete}/>
<Footer/>
