import React from 'react'

export const TodoItem = (props) => {
    return (
        <div>
            <h3 className="text-center">{props.todo.title}</h3> 
            <p>{props.todo.No}</p>
            
            <p>{props.todo.desc}</p>
            
            <button className="btn btn-danger" onClick={()=>props.onDelete(todo)}>Remove</button>
            <hr/>
        </div>
    )
}
